mapper.insert:

insert 
into tblapply (rsno, rcno, pid)
values(#{rsno}, #{rcno}, #{pid});

mapper.select:
select rsno, rcno, pid
from tblapply
where rcno=#{rcno}
AND pid=#{pid};

select readornot
from tblapply
where rcno=#{rcno}
AND rsno=#{rsno};

mapper.update:
update tblapply
set readornot = '읽음'
,userid = #{userid}
where rcno=#{rcno}
AND rsno=#{rsno};

mapper.delete:

delete from tblapply
where pid=#{pid}
AND rcno=#{rcno};
