mapper.update:

	update 
	tbluser
	set pref_job=#{pref_job}
	, pref_region=#{pref_region}
	, pref_employstatus=#{pref_employstatus}
	, pref_levelofeducation=#{pref_levelofeducation}
	, pref_lastcareer=#{pref_lastcareer}
	where id=#{id}

mapper.select:

	select distinct rc.bno as bno
	,SQRT(
	POW(((select z from tblcode where id=re.lastcareer)-(select z from tblcode where id=rc.exp)),2) * user.pref_lastcareer 
	+ POW(((select y from tblcode where id=re.levelofeducation)-(select y from tblcode where id=rc.edu)),2) * user.pref_levelofeducation
	+ POW(((select x from tblcode where id=re.employstatusid)-(select x from tblcode where id=rc.employstatusid)),2) * user.pref_employstatus
	) as distance
	from tblresume re, tblrecruit rc, tbluser user
	where re.bno=#{bno}
	and user.id=#{userid}
	order by distance asc
	limit 10
	;
