mapper.update:
		update tbluser
		set sessionKey = #{sessionId},
		sessionLimit = #{next}
		where id = #{id}

mapper.select:
select id
from tbluser
where pname = #{pname}
and email = #{email}

select id
from tbluser
where cname = #{cname}
and email = #{email}


	select count(mreadornot)
	from tblmessage
	where userid=#{id}
	and mreadornot=0
	;
