mapper.select:
select bno, title, cid, jobgroupid, jobgroupid2, rgbid, rgsid, jobdesc, recruitnum, employstatusid, salaryid, edu, exp, adddesc, period, acceptmethod, recruitform, regdate, viewcnt from tblrecruit where
jobgroupid2 = #{item}
;

select * from tblrecruit where jobgroupid2 = 1000 or
jobgroupid2 = #{item}
union select * from tblrecruit where jobgroupid in (select id2 from tbljobgroup where jobgroup='전체' and ( id = 1000 or
id = #{item}
)) order by bno ;

select bno, title, cid, jobgroupid, jobgroupid2, rgbid, rgsid, jobdesc, recruitnum, employstatusid, salaryid, edu, exp, adddesc, period, acceptmethod, recruitform, regdate, viewcnt from tblrecruit where
( rgbid = #{item.rgbid} and rgsid = #{item.rgsid})
;

select bno, title, cid, jobgroupid, jobgroupid2, rgbid, rgsid, jobdesc, recruitnum, employstatusid, salaryid, edu, exp, adddesc, period, acceptmethod, recruitform, regdate, viewcnt from tblrecruit where
employstatusid = #{item}
;

select bno, title, cid, jobgroupid, jobgroupid2, rgbid, rgsid, jobdesc, recruitnum, employstatusid, salaryid, edu, exp, adddesc, period, acceptmethod, recruitform, regdate, viewcnt from tblrecruit where
exp = #{item}
;

select bno, title, cid, jobgroupid, jobgroupid2, rgbid, rgsid, jobdesc, recruitnum, employstatusid, salaryid, edu, exp, adddesc, period, acceptmethod, recruitform, regdate, viewcnt from tblrecruit where
edu = #{item}
;

