mapper.update:
		update tbluser
		set sessionKey = #{sessionId},
		sessionLimit = #{next}
		where id = #{id}

mapper.select:
select id
from tbluser
where pname = #{pname}
and email = #{email}

select id
from tbluser
where cname = #{cname}
and email = #{email}


	select count(mreadornot)
	from tblmessage
	where userid=#{id}
	and mreadornot=1
	;

	
		select bno, userid, message, mreadornot, rcno, appliedpid
		from tblmessage
		where userid=#{userid}
		and mreadornot=1
		and bno<#{bno}
		order by bno desc
        limit 5
		;

mapper.update:

	update tblmessage
	set mreadornot= 0
	where bno=#{bno};
